app.controller("memo",["$scope","$stateParams","userAPIService","dialogService",function(n,t,i,r){n.userId=t.userId;n.title="备注";n.routeName="user/"+t.userId;i.getUser(n.userId).then(function(t){n.user=t.data});n.updateMemo=function(){var u={userId:t.userId,memo:n.user.memo};i.updateMemo(u).then(function(){r.showMention(1,"更新备注成功","user",{userId:t.userId},null)})}}]);