app.controller("appointments",["$scope","appointmentAPIService","$rootScope",function(n,t,i){function r(){t.getUserAppointments(n.date.format("YYYY-MM")).then(function(t){n.appointments=t.data})}i.pageTitle="预约记录";n.date=new Date;r();n.nextMonth=function(){n.date.setMonth(n.date.getMonth()+1,1);r()};n.previousMonth=function(){n.date.setMonth(n.date.getMonth()-1,1);r()}}]);